INSERT INTO ListItem (Identifier, ListId, Title, Summary, Body, CannotBeDeleted, ListItemTextTypeID) VALUES ('commercial-user-notes-intro', 1, 'Commercial User Notes Intro', 'The text shown above the user notes box on the select commercial options page.', 'Please provide additional information about where and how the image will be used. Where applicable please also provide the name of the organisation for whom the purchase is being made.<span class="required">*</span>', 1, 1);  
INSERT INTO ListItem (Identifier, ListId, Title, Summary, Body, CannotBeDeleted, ListItemTextTypeID) VALUES ('request-offline-payment-text', 1, 'Request Offline Payment text', 'The text shown by the checkbox to request offline payment.', 'You can request offline payment (by invoice) for your purchases by ticking this box. Please note that we are only able to offer this facility to organisations with a registered trading address in the UK. All other payments will need to be made online.', 1, 1);  
INSERT INTO ListItem (Identifier, ListId, Title, Summary, Body, CannotBeDeleted, ListItemTextTypeID) VALUES ('checkout', 2, 'Checkout help text', 'Checkout help text for pricebands', '<h2>Checkout</h2><h3>Purchase summary</h3><p>Shows the number of items in your purchase.</p><p>For non-commercial purchases the cost is shown as follows:</p><ul><li>Basket cost: the cost of items without VAT or shipping.</li><li>Shipping: applicable if purchasing prints.</li><li>Subtotal: total before tax.</li><li>VAT: at standard rate according to users region.</li><li>Total: including shipping and VAT.</li></ul><p>For commercial purchases, the cost is calculated when you select commercial options.</p><h3>Log in or Register</h3><p>If you are a new customer you need to supply at least your email address before you can purchase items.</p><p>If you already have an account then log in here. Fill in the forgotten password box if you need a password reminder.</p><h3>Shipping</h3><p>If you are purchasing prints, then you need to enter your delivery name and address. If the delivery details are the same as your home address, then check the "Save as my home address" box, to save the information in your profile for next time.</p><h3>Terms and conditions</h3><p>Please read the Terms and conditions of purchase. Click "I Agree" to accept.</p><h3>Ready to buy</h3><p>Check that the email address shown is yours, and confirm that the items listed in the "Purchase details" section are the ones that you wish to buy. Click the "Purchase" button to proceed with the purchase.</p><br/><h2>Non-commercial purchases</h2><p>When you click "Purchase", you will be taken to the payment service provider where you should make payment. If payment is successfully authorised, then you will see a screen called "Payment success". Click the link to return to your basket.</p><p>You will be sent an email confirming your purchase. If this is your first purchase, then you will also be sent an email with the details of your new account.</p><br/><h2>Commercial purchases</h2><p>Read through the options listed on the "Select Commercial Options" screen. Then for each item, select the commercial option that is closest to how you wish to use the item.</p><p>Sub Total is the cost of the options, and Total is the total with VAT.</p><p>Click "Submit" to make your request for the items. An administrator will deal with your request and may be in touch if they need to discuss the options selected.</p>', 1, 1);
INSERT INTO ListItem (Identifier, ListId, Title, Summary, Body, CannotBeDeleted, ListItemTextTypeID) VALUES ('purchases', 2, 'My Purchases help text', 'The help text for My Purchases', '<h2>My Downloads</h2><p>This page lists the downloads that you have purchased. By default, downloads purchased within the last three months are shown. If you want to see older purchases then change the date range and click "Go".</p><p>Follow the instructions given to download your items.<br/><br/><h2>My Orders</h2><p>This page lists all of your past orders. To filter by date, select a date range and click "Search".</p><p>The list shows the date and status of each order. Click on the "view" link to see the details of an order.</p><p>For prints, order status may be:</p> <ul><li><strong>Awaiting Fulfilment:</strong> an email will be sent to you when this order has been despatched.</li><li><strong>Fulfilled:</strong> this order has been completed.</li></ul><br/><p>For commercial purchases, order status may be: </p><ul><li><strong>Requires Approval:</strong> this order is being processed by the administrator.</li> <li><strong>Requires Online Payment:</strong> this order should be paid for online. Click on "view" to check order details, then click on "Make Payment".</li> <li><strong>Requires Offline Payment:</strong> this order should be paid for offline. An administrator will be in touch to arrange payment.</li> <li><strong>Paid For Online:</strong> this order has been paid for online.</li><li><strong>Paid For Offline:</strong> this order was paid for offline.</li><li><strong>Declined:</strong> this order was not completed.</li></ul><br/><br/><h2>Commercial Orders - Online Payment</h2><p>For orders marked "Requires Online Payment", view the order details and click "Make Payment".</p><p>On the "Order Payment" screen, check the "Order Summary" section, and read the Terms and Conditions applicable for your order. If you accept them then you will see the "Ready to buy" screen. Click "Purchase" to go to the payment provider where you can pay online. Click the link to return to the site and you will be able to download your items immediately.</p>', 1, 1);
